import React from "react"
import {useNavigate} from "react-router-dom"

import {IBlog} from "../interfaces/blog.interface"
import classes from "../css/modules/BlogCard.module.css"

interface BlogCardProps {
   blog: IBlog
}

export default function BlogCard({blog}: BlogCardProps) {

    const navigation = useNavigate()
    const clickHandler = () => navigation(`${blog.id}`)
    const maxCharacters = 80

    const truncatedCaption = blog.caption.length > maxCharacters ? `${blog.caption.slice(0, maxCharacters)}...` : blog.caption

    return (
        <div className={classes.card} onClick={clickHandler}>
            <p className={classes.heading}>
                {truncatedCaption}
            </p>
        </div>
    )
}
