import React, {useEffect, useRef} from "react"
import ReactPaginate from "react-paginate"

import {useAppDispatch, useAppSelector} from "../hooks/redux"
import {fetchDestinations} from "../store/actions/destinationActions"
import BlogCard from "../components/BlogCard"

const ITEMS_PER_PAGE: number = 50

export default function DestinationPage() {

    const page = useRef(1)
    const dispatch = useAppDispatch()
    const {error, loading, destinations, count} = useAppSelector(state => state.destination)

    const pageCount = Math.ceil(count / ITEMS_PER_PAGE)

    const pageChangeHandler = ({selected}: { selected: number }) => {
        page.current = selected
    }

    useEffect(() => {
        dispatch(fetchDestinations(page.current, ITEMS_PER_PAGE))
    }, [dispatch, page])

    return (
        <div className="container mx-auto max-w-[760px] pt-5">
            { loading && <p className="text-center text-lg">Loading...</p> }
            { error && <p className="text-center text-red-600">{error}</p> }

            { destinations.map(destination => <BlogCard key={destination.id} blog={destination} />) }

            <ReactPaginate
                breakLabel="..."
                nextLabel="next >"
                onPageChange={pageChangeHandler}
                pageRangeDisplayed={5}
                forcePage={page.current - 1}
                pageCount={pageCount}
                previousLabel="< previous"
                containerClassName="flex"
                pageClassName="py-2 px-2 border mr-2"
                previousClassName="py-2 px-2 border mr-2"
                nextClassName="py-2 px-2 border mr-2"
                activeClassName="bg-gray-500 text-white"
            />
        </div>
    )
}
