import React, {useEffect, useState} from "react"
import {useParams} from "react-router-dom"
import {IBlog} from "../models/model"
import axios from "../axios"

export default function BlogDetailPage() {
    const params = useParams<"id">()

    const [blog, setBlogs] = useState<IBlog | null>(null)
    const [loading, setLoading] = useState(true)

    const fetchDetailBlog = async () => {
        const response = await axios.get("https://dummyjson.com/products")
        setBlogs(response.data.products)

        if (params.id) {
            console.log(response.data.products[params.id - 1])
            setBlogs(response.data.products[params.id - 1])
        }

        setLoading(false)
    }

    useEffect(() => {
        fetchDetailBlog()
    }, [])

    if (loading) {
        return <p className="text-center">Loading...</p>
    }

    return (
        <div className="container mx-auto max-w-[760px] pt-5">
            <h1 className="text-center text-2xl">{blog?.title}</h1>

            <p>{blog?.description}</p>
            <p>{blog?.price}</p>
        </div>
    )
}
