import {createSlice, PayloadAction} from "@reduxjs/toolkit"
import {IBlog} from "../../interfaces/blog.interface"

interface BlogState {
    loading: boolean
    error: string
    blogs: IBlog[]
    blog: IBlog,
    pagination: {
        after: string
        before: string
    }
}

const initialBlog: IBlog = {
    id: "",
    media_type: "",
    media_url: "",
    caption: "",
    timestamp: "",
    permalink: ""
}

const initialState: BlogState = {
    loading: false,
    error: "",
    blogs: [],
    blog: initialBlog,
    pagination: {
        after: "",
        before: ""
    }
}

interface BlogsPayload {
    blogs: IBlog[]
    pagination: {
        after: string
        before: string
    }
}

export const blogSlice = createSlice({
    name: "blog",
    initialState: initialState,
    reducers: {
        fetching(state) {
            state.loading = true
        },
        fetchSuccess(state, action: PayloadAction<BlogsPayload>) {
            state.loading = false
            state.blogs = action.payload.blogs
            state.pagination.after = action.payload.pagination.after
            state.pagination.before = action.payload.pagination.before
            state.error = ""
        },
        fetchError(state, action: PayloadAction<Error>) {
            state.loading = false
            state.error = action.payload.message
        }
    }
})

export default blogSlice.reducer
