import {fetchBlog} from "../store/actions/blogActions"


export default function BlogDetailPage() {
    const params: any = useParams<"id">()
    const dispatch = useAppDispatch()
    const {blogs} = useAppSelector(state => state.blog)

    const fetchDetailBlog = async () => {
        const response = await axios.get("products")
        setBlogs(response.data.products)

        if (params.id) {
            setBlogs(response.data.products[params.id - 1])
        }

        setLoading(false)
    }


    return (
        <div className="container mx-auto max-w-[760px] pt-5">

            <h1 className="text-center text-2xl">{blog.caption}</h1>
        </div>
    )
}
