import React, {useEffect} from "react"
import {useParams} from "react-router-dom"

import {useAppDispatch, useAppSelector} from "../hooks/redux"
import {fetchBlog} from "../store/actions/blogActions"
import {blogSlice} from "../store/slices/blogSlice";


export default function BlogDetailPage() {
    const params: any = useParams<"id">()
    const dispatch = useAppDispatch()
    const {blog} = useAppSelector(state => state.blog)

    useEffect(() => {
        dispatch(blogSlice.actions.filter({id: params.id}))
    }, [params.id])

    return (
        <div className="container mx-auto max-w-[760px] pt-5">

            <h1 className="text-center text-2xl">{blog[0].caption}</h1>
        </div>
    )
}
