import React, {useEffect, useState} from "react"
import {useParams} from "react-router-dom"
import {IBlog} from "../models/model"
import axios from "../axios"

export default function BlogDetailPage() {
    const params = useParams<"id">()

    const [airport, setAirports] = useState<IBlog | null>(null)
    const [loading, setLoading] = useState(true)

    const fetchDetailAirport = async () => {
        const response = await axios.get("https://dummyjson.com/products")
        setAirports(response.data.products)

        if (params.id) setAirports(response.data.products[params.id])
        setLoading(false)
    }

    useEffect(() => {
        fetchDetailAirport()
        console.log(airport)
    }, [params])

    if (loading) return <p className="text-center">Loading...</p>

    return (
        <div className="container mx-auto max-w-[760px] pt-5">

        </div>
    )
}
