import {createSlice, PayloadAction} from "@reduxjs/toolkit"
import {IBlog} from "../../interfaces/blog.interface"

interface BlogState {
    loading: boolean
    error: string
    blogs: IBlog[]
    blog: IBlog
}

const initialBlog: IBlog = {
    id: "",
    media_type: "",
    media_url: "",
    caption: "",
    timestamp: "",
    permalink: ""
}

const initialState: BlogState = {
    loading: false,
    error: "",
    blogs: [],
    blog: initialBlog
}

interface BlogsPayload {
    blogs: IBlog[]
}

interface BlogPayload {
    id: string
}


export const blogSlice = createSlice({
    name: "blog",
    initialState: initialState,
    reducers: {
        fetching(state) {
            state.loading = true
        },
        fetchSuccess(state, action: PayloadAction<BlogsPayload>) {
            state.loading = false
            state.blogs = action.payload.blogs
            state.error = ""
        },
        fetchError(state, action: PayloadAction<Error>) {
            state.loading = false
            state.error = action.payload.message
        },
        filter(state, action: PayloadAction<BlogPayload>) {
            state.blog = state.blogs.filter(blog => blog.id === action.payload.id)
            console.log(state.blog)
        }
    }
})

export default blogSlice.reducer
