import React, {useEffect, useRef} from "react"
import ReactPaginate from "react-paginate"

import {IToken} from "../interfaces/blog.interface"
import BlogCard from "../components/BlogCard"
import {useAppDispatch, useAppSelector} from "../hooks/redux"
import {useLazyRefreshTokenQuery, useGetProfileMediaQuery} from "../services/instagram.api"
import {blogSlice} from "../store/slices/blogSlice"
import config from "../assets/config.json"

const ITEMS_PER_PAGE: number = 50

export default function BlogPage() {

    const page = useRef(1)
    const [refreshToken] = useLazyRefreshTokenQuery()
    const  {isSuccess, isLoading, error, data: blogs} = useGetProfileMediaQuery("")

    const dispatch = useAppDispatch()
    const pageCount = Math.ceil(1 / ITEMS_PER_PAGE)

    const pageChangeHandler = ({selected}: { selected: number }) => {
        const {pagination} = useAppSelector(state => state.blog)

    }

    const refreshBlogToken = async () => {
        try {
            const newToken: IToken = await refreshToken("")
            config.instagramToken = newToken.access_token
        } catch (error) {
            console.error("Error refreshing token:", error)
        }
    }

    const setupTokenRefresh = (expiresIn: number) => {
        const refreshTokenInterval = expiresIn * 1000; // Преобразуем expiresIn в миллисекунды

        const tokenRefreshTimer = setInterval(() => refreshBlogToken(), refreshTokenInterval)
        return tokenRefreshTimer
    }

    useEffect(() => {
        if (isSuccess) {
            dispatch(blogSlice.actions.fetchSuccess({blogs: blogs.data, pagination: blogs.paging.cursors}))
        }
    }, [isSuccess])

    return (
        <div className="container mx-auto max-w-[760px] pt-5">
            { isLoading && <p className="text-center text-lg">Loading...</p> }
            { error && <p className="text-center text-red-600">{ error.data }</p> }

            { isSuccess && blogs.data.map(blog => <BlogCard key={blog.id} blog={blog} />) }

            <ReactPaginate
                breakLabel="..."
                nextLabel="next >"
                onPageChange={pageChangeHandler}
                pageRangeDisplayed={5}
                forcePage={page.current - 1}
                pageCount={pageCount}
                previousLabel="< previous"
                containerClassName="flex"
                pageClassName="py-2 px-2 border mr-2"
                previousClassName="py-2 px-2 border mr-2"
                nextClassName="py-2 px-2 border mr-2"
                activeClassName="bg-gray-500 text-white"
            />
        </div>
    )
}
