import {createApi, fetchBaseQuery} from "@reduxjs/toolkit/query/react"
import {IBlog} from "../interfaces/blog.interface"
import config from "../assets/config.json"


export const instagramApi = createApi({
    reducerPath: "instagram/api",
    baseQuery: fetchBaseQuery({
        baseUrl: "https://graph.instagram.com"
    }),
    refetchOnFocus: true,
    endpoints: build => ({
        refreshToken: build.query<[], string>({
            query: () => ({
                url: `refresh_access_token?grant_type=ig_refresh_token&access_token=${config.instagramToken}`
            }),
            transformResponse: (response: any) => response.items
        }),
        getProfileMedia: build.query<IBlog[], string>({
            query: () => ({
                url: `me/media?fields=id,media_type,media_url,caption,timestamp,thumbnail_url,permalink&access_token=${config.instagramToken}`
            }),
            transformResponse: (response: any) => response.data

        })
    })
})

export const {useGetProfileMediaQuery, useLazyRefreshTokenQuery} = instagramApi
